let API_KEY="dd9e2e680e4c75d60ea32403d32379e5",city="Dnipro";function getDate(){var e=new Date,t=`${e.getDate().toString().padStart(2,"0")}.${(e.getMonth()+1).toString().padStart(2,"0")}.`+e.getFullYear(),e=e.getHours().toString().padStart(2,"0")+":"+e.getMinutes().toString().padStart(2,"0");document.getElementById("date").innerText="Date: "+t,document.getElementById("time").innerText="Time: "+e,getWeather()}async function getWeather(){try{var e=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=Dnipro&lang=eng&appid=${API_KEY}&units=metric`);if(!e.ok)throw new Error("Network response was not ok: "+e.statusText);var t=await e.json(),n=`http://openweathermap.org/img/wn/${t.weather[0].icon}.png`;document.getElementById("weatherIcon").src=n,document.getElementById("weatherIcon").alt=t.weather[0].description,document.getElementById("city").innerText=t.name,document.getElementById("temperature").innerText=`Temperature: ${t.main.temp}°C`,document.getElementById("feels_like").innerText=`Feels like: ${t.main.feels_like}°C`,document.getElementById("description").innerText="Description: "+t.weather[0].description,document.getElementById("humidity").innerText=`Humidity: ${t.main.humidity}%`,document.getElementById("pressure").innerText=`Pressure: ${t.main.pressure} hPa`,document.getElementById("wind").innerText=`Wind: ${t.wind.speed} km/h`}catch(e){console.error("Failed to fetch weather datas: ",e)}}document.addEventListener("DOMContentLoaded",()=>{getDate(),document.getElementById("updateButton").addEventListener("click",getDate)});